import { useState } from "react";
export default function Providing() {
  const [counter, setCounter] = useState(0);
  function handleClick(e) {
    e.perventDefault();
    setCounter(counter + 1);
  }

  return (
    <>
      <button onClick={handleClick}>+1</button>
      <p>{counter}</p>
    </>
  );
}
